        $this->execute("INSERT INTO `translate` VALUES (656,13,'textarea','cms_sendlogin_mail','','Hierbij ontvang je een wachtwoord om in te loggen op het systeem van {sitename}.<br /><br /> Je nieuwe wachtwoord is <strong>{password}</strong><br /><br /> Let op: Het systeem maakt onderscheid tussen hoofd- en kleine letters.<br /><br /> Als je bent ingelogd kan je het wachtwoord zelf wijzigen, door op je naam te klikken in de rechterbovenhoek en dan voor \'Instellingen\' te kiezen. Kies dan wel voor een niet al te makkelijk te raden password van minimaal acht tekens.','Hereby your password to login to {sitename}.<br /><br /> Your password is <strong>{password}</strong><br /><br /> After logging in you can change your password by clicking on your name in the upper right corner and choose \'settings\'.','',0,NULL,NULL,NULL,NULL,'0000-00-00 00:00:00'),(657,13,'text','cms_sendlogin_mailsubject','','Nieuw wachtwoord','New password','',0,NULL,NULL,NULL,NULL,'0000-00-00 00:00:00'),(658,13,'text','cms_sendlogin_confirm',NULL,'De logingegevens zijn verstuurd.','Within a few minutes you will receive an e-mail with further instructions to reset your password.','',0,NULL,NULL,NULL,NULL,'0000-00-00 00:00:00'),(660,13,'text','cms_email_signature','','Met veel liefde...','Much love...','',0,NULL,NULL,NULL,NULL,'0000-00-00 00:00:00'),(661,13,'text','buytickets',NULL,'Koop kaarten voor deze voorstelling','','',0,NULL,NULL,NULL,NULL,'0000-00-00 00:00:00'),(662,13,'text','cms_form_changefile',NULL,'Wijzig bestand','Change file','',0,NULL,NULL,NULL,NULL,'0000-00-00 00:00:00'),(663,13,'text','cms_form_changeimage',NULL,'Wijzig afbeelding','Change image','',0,NULL,NULL,NULL,NULL,'2017-04-10 19:54:00'),(664,13,'text','cms_form_delete',NULL,'Verwijderen','Delete','',0,NULL,NULL,NULL,NULL,'0000-00-00 00:00:00'),(665,13,'text','cms_form_wipe','','Wissen','Wipe','',0,NULL,NULL,NULL,NULL,'0000-00-00 00:00:00'),(666,13,'text','cms_form_file_deletesuccess',NULL,'bestand verwijderd','File removed